import React from 'react'
import AddContact from './AddContact'
import {useSelector, useDispatch} from 'react-redux'
import {removeContact} from '../actions/contacts'

const Contacts = () => {

  const contactList = useSelector(state => state.contacts) 
  const dispatch = useDispatch()// store.dispatch(actionCreator)

  return (
    <>
      <h1>Contacts and Redux</h1>

      <ul>
        {contactList.map(contactObj =>{
          return <li key={contactObj.id}>{contactObj.firstName} {contactObj.city}


            <a href="#" onClick={()=>dispatch(removeContact(contactObj.id))}>X</a>
          </li>
        })}
      </ul>

      <AddContact />
    </>
  )
}

export default Contacts
