const reducers = (state, action) => {

    if(state == undefined){
        state = {
            count: 23,

        }
    }


    switch (action.type) {
        case "INCREMENT":
            return {
                ...state, 
                count: state.count + action.data
            }


        default:
            return state;
    }
}

export default reducers
