import React from 'react'
import {useDispatch, useSelector} from 'react-redux'

const CountHooks = () => {

  const count = useSelector(state => state.count) // count is mapped from global state
  return (
    <>

      <h2>{count}</h2>
    </>
  )
}

export default CountHooks
